import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useHistory, Link } from 'react-router-dom';
import Picker from 'react-mobile-picker';

function Register() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [babyBirthday, setBabyBirthday] = useState('');
  const [showPicker, setShowPicker] = useState(false);

  // 날짜 선택기 옵션들
  const [pickerValue, setPickerValue] = useState({
    year: new Date().getFullYear().toString(),
    month: '1',
    day: '1'
  });

  // 선택 가능한 옵션들
  const [pickerOptions, setPickerOptions] = useState({
    year: Array.from({ length: 100 }, (_, i) => (new Date().getFullYear() - i).toString()),
    month: Array.from({ length: 12 }, (_, i) => (i + 1).toString()),
    day: Array.from({ length: 31 }, (_, i) => (i + 1).toString())
  });

  // 월이 변경될 때 해당 월의 일수 업데이트
  useEffect(() => {
    const year = parseInt(pickerValue.year);
    const month = parseInt(pickerValue.month);
    const daysInMonth = new Date(year, month, 0).getDate();

    setPickerOptions(prev => ({
      ...prev,
      day: Array.from({ length: daysInMonth }, (_, i) => (i + 1).toString())
    }));
  }, [pickerValue.year, pickerValue.month]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const birthDate = new Date(
        parseInt(pickerValue.year),
        parseInt(pickerValue.month) - 1,
        parseInt(pickerValue.day)
      );

      const response = await axios.post('http://localhost:5001/api/auth/register', {
        email,
        password,
        babyBirthday: birthDate
      });
      console.log('회원가입 응답:', response.data);
      alert('회원가입이 완료되었습니다.');
      history.push('/login');
    } catch (error) {
      console.error('회원가입 에러:', error);
      const errorMessage = error.response?.data?.message || '회원가입에 실패했습니다.';
      alert(errorMessage);
    }
  };

  return (
    <div className="auth-page">
      <div className="auth-container">
        <div className="auth-header">
          <h2>환영합니다!</h2>
          <p className="auth-subtitle">우리 아이의 특별한 순간을 기록해보세요</p>
        </div>
        <form onSubmit={handleSubmit} className="auth-form">
          <div className="form-floating">
            <input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="이메일을 입력하세요"
              required
            />
            <label htmlFor="email">이메일</label>
            <div className="form-icon">📧</div>
          </div>
          <div className="form-floating">
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="비밀번호를 입력하세요"
              required
            />
            <label htmlFor="password">비밀번호</label>
            <div className="form-icon">🔒</div>
          </div>
          <div className="form-floating date-picker-container">
            <div 
              className="date-picker-trigger"
              onClick={() => setShowPicker(true)}
            >
              {pickerValue.year}년 {pickerValue.month}월 {pickerValue.day}일
            </div>
            <div className="form-icon">👶</div>
          </div>
          {showPicker && (
            <div className="picker-modal">
              <div className="picker-modal-backdrop" onClick={() => setShowPicker(false)} />
              <div className="picker-modal-content">
                <Picker
                  value={pickerValue}
                  onChange={setPickerValue}
                  height={150}
                  wheelMode="natural"
                  optionGroups={pickerOptions}
                />
                <button 
                  className="picker-confirm-btn"
                  onClick={() => setShowPicker(false)}
                >
                  확인
                </button>
              </div>
            </div>
          )}
          <button type="submit" className="auth-button">
            회원가입
            <span className="button-icon">→</span>
          </button>
          <div className="auth-footer">
            <p>이미 계정이 있으신가요? <Link to="/login">로그인</Link></p>
          </div>
        </form>
      </div>
    </div>
  );
}

export default Register;
