import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useHistory } from 'react-router-dom';

function DiaryList() {
  const [diaries, setDiaries] = useState([]);
  const [showList, setShowList] = useState(false);
  const [selectedDiary, setSelectedDiary] = useState(null);
  const [chatInput, setChatInput] = useState('');
  const [chatResponse, setChatResponse] = useState(null);
  const history = useHistory();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      history.push('/login');
      return;
    }
    if (showList) {
      fetchDiaries();
    }
  }, [showList, history]);

  const fetchDiaries = async () => {
    try {
      const response = await axios.get('http://localhost:5001/api/diary', {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });
      setDiaries(response.data);
    } catch (error) {
      console.error('ÏùºÍ∏∞ Î™©Î°ùÏùÑ Î∂àÎü¨Ïò§ÎäîÎç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§:', error);
    }
  };

  const handleDiaryClick = (diary) => {
    setSelectedDiary(diary);
  };

  const handleClosePopup = () => {
    setSelectedDiary(null);
  };

  // ÌåùÏóÖ Ïô∏Î∂Ä ÌÅ¥Î¶≠ Ïãú Îã´Í∏∞
  const handleBackdropClick = (e) => {
    if (e.target === e.currentTarget) {
      handleClosePopup();
    }
  };

  const handleChatSubmit = async () => {
    if (!chatInput.trim()) return;

    try {
      const response = await axios.post('http://localhost:5001/api/chat', {
        question: chatInput
      });

      setChatResponse({
        text: response.data.answer,
        loading: false
      });
      setChatInput('');
    } catch (error) {
      console.error('Ï±óÎ¥á ÏùëÎãµ ÏóêÎü¨:', error);
      setChatResponse({
        text: "Ï£ÑÏÜ°Ìï©ÎãàÎã§. ÎãµÎ≥ÄÏùÑ ÏÉùÏÑ±ÌïòÎäî Ï§ë Î¨∏Ï†úÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.",
        loading: false,
        error: true
      });
    }
  };

  if (!showList) {
    return (
      <div className="main-container">
        <div className="main-buttons">
          <Link to="/create-diary" className="main-button">
            <div className="button-icon">‚úèÔ∏è</div>
            <div className="button-text">Ïò§Îäò Í∏∞Î°ùÌïòÍ∏∞</div>
            <div className="button-description">ÏÉàÎ°úÏö¥ ÏàúÍ∞ÑÏùÑ Í∏∞Î°ùÌï¥Î≥¥ÏÑ∏Ïöî</div>
          </Link>
          <div className="main-button" onClick={() => setShowList(true)}>
            <div className="button-icon">üìñ</div>
            <div className="button-text">Îã§Ïãú ÏÇ¥Ìé¥Î≥¥Í∏∞</div>
            <div className="button-description">ÏÜåÏ§ëÌïú ÏàúÍ∞ÑÎì§ÏùÑ Îã§Ïãú ÎßåÎÇòÎ≥¥ÏÑ∏Ïöî</div>
          </div>
        </div>

        <div className="chatbot-section">
          <div className="chatbot-container">
            <div className="chatbot-header">
              <span className="chatbot-icon">ü§ñ</span>
              <h3>Ïú°ÏïÑ ÎèÑÏö∞ÎØ∏</h3>
            </div>
            <div className="chatbot-search">
              <input
                type="text"
                placeholder="Ïú°ÏïÑÏóê ÎåÄÌï¥ Î¨¥ÏóáÏù¥Îì† Î¨ºÏñ¥Î≥¥ÏÑ∏Ïöî!"
                className="chatbot-input"
                value={chatInput}
                onChange={(e) => setChatInput(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && handleChatSubmit()}
              />
              <button className="chatbot-button" onClick={handleChatSubmit}>
                <span className="button-icon">üí¨</span>
                ÏßàÎ¨∏ÌïòÍ∏∞
              </button>
            </div>
            {chatResponse && (
              <div className={`chatbot-response ${chatResponse.loading ? 'loading' : ''} ${chatResponse.error ? 'error' : ''}`}>
                {chatResponse.loading ? (
                  <div className="loading-indicator">
                    <span>ÎãµÎ≥Ä ÏÉùÏÑ± Ï§ë</span>
                    <div className="loading-dots">
                      <span>.</span><span>.</span><span>.</span>
                    </div>
                  </div>
                ) : (
                  <>
                    <div className="response-icon">
                      {chatResponse.error ? '‚ùå' : 'üí°'}
                    </div>
                    <p>{chatResponse.text}</p>
                  </>
                )}
              </div>
            )}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="diary-list-container">
      <h2>Ïö∞Î¶¨ ÏïÑÏù¥Ïùò ÏÑ±Ïû•ÏùºÍ∏∞</h2>
      <div className="diary-grid">
        {diaries.map((diary) => (
          <div 
            key={diary._id} 
            className="diary-card"
            onClick={() => handleDiaryClick(diary)}
          >
            {diary.imageUrl && (
              <div className="diary-card-image">
                <img src={`http://localhost:5001${diary.imageUrl}`} alt="ÏùºÍ∏∞ Ïù¥ÎØ∏ÏßÄ" />
              </div>
            )}
            <div className="diary-card-content">
              <h3>{diary.title}</h3>
              <p className="diary-preview">{diary.content}</p>
              <div className="diary-date">
                {new Date(diary.createdAt).toLocaleDateString()}
              </div>
            </div>
          </div>
        ))}
      </div>

      {selectedDiary && (
        <div className="diary-popup-backdrop" onClick={handleBackdropClick}>
          <div className="diary-popup">
            <button className="popup-close" onClick={handleClosePopup}>√ó</button>
            <div className="popup-content">
              {selectedDiary.imageUrl && (
                <div className="popup-image">
                  <img 
                    src={`http://localhost:5001${selectedDiary.imageUrl}`} 
                    alt="ÏùºÍ∏∞ Ïù¥ÎØ∏ÏßÄ"
                  />
                </div>
              )}
              <div className="popup-text">
                <h2>{selectedDiary.title}</h2>
                <div className="popup-date">
                  {new Date(selectedDiary.createdAt).toLocaleDateString()}
                </div>
                <p>{selectedDiary.content}</p>
              </div>
            </div>
          </div>
        </div>
      )}

      <button className="back-button" onClick={() => setShowList(false)}>
        Î©îÏù∏ÏúºÎ°ú ÎèåÏïÑÍ∞ÄÍ∏∞
      </button>
    </div>
  );
}

export default DiaryList;
