import React, { useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

function DiaryForm() {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [image, setImage] = useState(null);
  const [showSuccess, setShowSuccess] = useState(false);
  const history = useHistory();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('title', title);
    formData.append('content', content);
    formData.append('image', image);

    try {
      await axios.post('http://localhost:5001/api/diary', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      // ÏÑ±Í≥µ Î©îÏãúÏßÄ ÌëúÏãú
      setShowSuccess(true);

      // 3Ï¥à ÌõÑ Î©îÏù∏ ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
      setTimeout(() => {
        history.push('/');
      }, 3000);
    } catch (error) {
      alert('ÏùºÍ∏∞ ÏûëÏÑ±Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
    }
  };

  return (
    <div className="diary-form">
      {showSuccess && (
        <div className="success-message">
          <span className="success-icon">‚ú®</span>
          <p>ÏÜåÏ§ëÌïú ÏàúÍ∞ÑÏù¥ Ï†ÄÏû•ÎêòÏóàÏäµÎãàÎã§!</p>
          <span className="success-icon">‚ú®</span>
        </div>
      )}
      <h2>Ïò§ÎäòÏùò ÌäπÎ≥ÑÌïú ÏàúÍ∞Ñ</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Ï†úÎ™©</label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Ï†úÎ™©ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
            required
          />
        </div>
        <div className="form-group">
          <label>ÎÇ¥Ïö©</label>
          <textarea
            value={content}
            onChange={(e) => setContent(e.target.value)}
            placeholder="Ïò§ÎäòÏùò ÌäπÎ≥ÑÌïú ÏàúÍ∞ÑÏùÑ Í∏∞Î°ùÌï¥Î≥¥ÏÑ∏Ïöî"
            required
          />
        </div>
        <div className="form-group">
          <label>ÏÇ¨ÏßÑ</label>
          <label className="file-input-label">
            <input
              type="file"
              onChange={(e) => setImage(e.target.files[0])}
              accept="image/*"
              className="file-input"
            />
            <span className="file-input-icon">üì∏</span>
            <span className="file-input-text">
              {image ? image.name : "ÏÇ¨ÏßÑÏùÑ ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî"}
            </span>
          </label>
        </div>
        <button type="submit">
          ÏàúÍ∞ÑÏùÑ Í∏∞Î°ùÌïòÍ∏∞
          <span>üìù</span>
        </button>
      </form>
    </div>
  );
}

export default DiaryForm;
