import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

function DiaryListView() {
  const [diaries, setDiaries] = useState([]);
  const [loading, setLoading] = useState(true);
  const history = useHistory();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      history.push('/login');
      return;
    }

    const fetchDiaries = async () => {
      try {
        setLoading(true);
        console.log('토큰:', token);

        const response = await axios.get('http://localhost:5001/api/diary', {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        });
        console.log('서버 응답:', response.data);
        setDiaries(response.data);
      } catch (error) {
        console.error('에러 상세:', error.response || error);
        if (error.response?.status === 403) {
          localStorage.removeItem('token');
          history.push('/login');
        }
      } finally {
        setLoading(false);
      }
    };

    fetchDiaries();
  }, [history]);

  if (loading) {
    return (
      <div className="diary-list-view loading">
        <div className="loading-spinner">로딩 중...</div>
      </div>
    );
  }

  return (
    <div className="diary-list-view">
      <h2>우리 아이의 소중한 순간들</h2>
      {diaries.length === 0 ? (
        <div className="no-diaries">
          <p>아직 작성된 일기가 없습니다.</p>
          <button onClick={() => history.push('/create-diary')}>
            첫 일기 작성하기
          </button>
        </div>
      ) : (
        <div className="diary-grid">
          {diaries.map((diary) => (
            <div key={diary._id} className="diary-card">
              <div className="diary-card-image">
                {diary.imageUrl && (
                  <img src={`http://localhost:5001${diary.imageUrl}`} alt="일기 이미지" />
                )}
              </div>
              <div className="diary-card-content">
                <h3>{new Date(diary.createdAt).toLocaleDateString()}</h3>
                <h4>{diary.title}</h4>
                <p>{diary.content}</p>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default DiaryListView;
