import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

function DiaryList() {
  const [diaries, setDiaries] = useState([]);
  const [showList, setShowList] = useState(false);
  const history = useHistory();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      history.push('/login');
      return;
    }

    if (showList) {
      fetchDiaries();
    }
  }, [showList, history]);

  const fetchDiaries = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5001/api/diary', {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      setDiaries(response.data);
    } catch (error) {
      console.error('일기 목록을 불러오는데 실패했습니다:', error);
    }
  };

  const handleTodayClick = () => {
    history.push('/create-diary');
  };

  const handleHistoryClick = () => {
    setShowList(true);
  };

  if (!showList) {
    return (
      <div className="main-buttons-container">
        <h1>우리 아이의 소중한 순간</h1>
        <div className="main-buttons">
          <button className="main-button today" onClick={handleTodayClick}>
            <span className="button-icon">📝</span>
            <span className="button-text">오늘 기록하기</span>
            <span className="button-description">오늘의 특별한 순간을 기록해보세요</span>
          </button>
          <button className="main-button history" onClick={handleHistoryClick}>
            <span className="button-icon">📚</span>
            <span className="button-text">과거 찾아보기</span>
            <span className="button-description">소중한 추억을 다시 살펴보세요</span>
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="diary-list-container">
      <h2>우리 아이의 성장일기</h2>
      <div className="diary-grid">
        {diaries.map((diary) => (
          <div key={diary._id} className="diary-card">
            <div className="diary-card-image">
              {diary.imageUrl && (
                <img src={`http://localhost:5001${diary.imageUrl}`} alt="일기 이미지" />
              )}
            </div>
            <div className="diary-card-content">
              <h3>{new Date(diary.createdAt).toLocaleDateString()}</h3>
              <p>{diary.content}</p>
            </div>
          </div>
        ))}
      </div>
      <button className="back-button" onClick={() => setShowList(false)}>
        메인으로 돌아가기
      </button>
    </div>
  );
}

export default DiaryList;
