import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

function DiaryList() {
  const [diaries, setDiaries] = useState([]);
  const history = useHistory();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      history.push('/login');
      return;
    }

    const fetchDiaries = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/diary', {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        setDiaries(response.data);
      } catch (error) {
        console.error('일기 목록을 불러오는데 실패했습니다:', error);
      }
    };

    fetchDiaries();
  }, [history]);

  return (
    <div className="diary-list">
      <h2>육아일기 목록</h2>
      {diaries.map((diary) => (
        <div key={diary._id} className="diary-item">
          <h3>{diary.title}</h3>
          <p>{diary.content}</p>
          {diary.imageUrl && (
            <img src={`http://localhost:5001${diary.imageUrl}`} alt="일기 이미지" />
          )}
          <p className="diary-date">
            {new Date(diary.createdAt).toLocaleDateString()}
          </p>
        </div>
      ))}
    </div>
  );
}

export default DiaryList;
